// Función principal que se auto-ejecuta
(function addHelloWorldToBody() {
    /**
     * Crea un elemento div con el texto "Hello, World!" y lo añade al body del documento.
     */
    function createHelloWorldElement() {
        // Crear el elemento div
        const helloWorldDiv = document.createElement('div');
        
        // Establecer el texto del elemento
        helloWorldDiv.textContent = `Hello, World!`;
        
        // Añadir el elemento al body del documento
        document.body.appendChild(helloWorldDiv);
    }
    
    // Ejecutar la función para crear y añadir el elemento
    createHelloWorldElement();
})();
